import React, { Component } from 'react'

export default class App extends Component { 

  constructor(props) {
    super(props);
    this.state = {
      items: []
    };
  }

  componentDidMount() {
    fetch("https://type.fit/api/quotes")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            items: result
          });
        },
      );
  }

  render() {
    const { items } = this.state;
   
      return (
        {items,}
      );
    
  }  
}
