function UseState({text,maxLength}) {
    const [hidden, sethidden] = useState(true);
      if (text.length <= maxLength) {
    return <span>{text}</span>;
  }

    return (
        <p>{text.length <= maxLength ? text : text.substring(0,maxLength)} {hidden?<a href="#" onClick={()=>sethidden(false)}>...See More</a>: <a href="#" onClick={()=>sethidden(true)}>...See Less</a>}</p>
            
    )
}

export default UseState
