import React, {useState} from 'react'


function UseState({text,maxLength}) {
    const [hidden, sethidden] = useState(true);
    

    return (
        <p> {text.length <= maxLength ? text : text.substring(0,maxLength)} {hidden ? <a href="#" onClick={()=>{sethidden(false);document.querySelector('').innerText = hidden ? {text} : text.substring(0,maxLength)}}>...See More</a> 
        : <a href="#" onClick={()=>sethidden(true)}>...See Less</a>}</p>
            
    )
}

export default UseState
