import React, { useState } from "react";
import Nav from "./Components/Nav";
import ItemPage from "./Components/ItemPage";
import CartPage from "./Components/CartPage";
import { items } from "./StaticData";
const summarizeCart = (cart) => {
  const groupedItems = cart.reduce((summary, item) => {
    summary[item.id] = summary[item.id] || {
      ...item,
      count: 0,
    };
    summary[item.id].count++;
    return summary;
  }, {});
  return Object.values(groupedItems);
};
export default function App() {
  const [activeTab, setActiveTab] = useState("cart");
  const [cart, setCart] = useState([]);
  const addToCart = (item) => {
    setCart((prevCart) => [...prevCart, item]);
  };
  const removeItem = item => {
    let index = cart.findIndex(i => i.id === item.id);
    if (index >= 0) {
    setCart(cart => {
    const copy = [...cart];
    copy.splice(index, 1);
    return copy;
    });
    }
    };

  return (
    <div className="App">
      <Nav activeTab={activeTab} onTabChange={setActiveTab} />
      <main className="app-content">
        <Content tab={activeTab} onAddToCart={addToCart}
                  cart={summarizeCart(cart)} />
      </main>
    </div>
  );
}

const Content = ({ tab, onAddToCart,onRemoveOne,cart,onRemoveItem}) => {
  switch (tab) {
    default:
    case "items":
      return <ItemPage items={items} onAddToCart={onAddToCart} />;
    case "cart":
      return <CartPage items={cart} 
      onAddOne={onAddToCart}
onRemoveOne={onRemoveItem}
/>;
  }
};
