import React, { Component } from 'react'

export default class App extends Component {
    constructor(){
        super();
        this.state = {
            value : ''
        }
        this.textInput = React.createRef();
    }
    render() {
        return (
            <div>
                <form className="p-2">
                You Typed : {this.state.value}<br/>
                Input : <input type="text" /> <br/>
                <input type="submit" value="Submit" />
                </form>
                </div>
        )
    }
}
