import React, { Component } from 'react';
import Student from './Student';

class App extends Component {
   constructor(props){
       super(props);
       this.state = {
           name:this.props.name,
           roll : 22
       }
   }
   handleClick = ()=>{
       this.setState({roll:111,name:'shohid'})
   }
    render() { 
        return (
            <>
            <Student name="Tahid"/>
            <button onClick={this.handleClick}>Click me</button>
            </>
          );
    }
}
 
export default App;
