import React, { Component } from 'react';

class App extends Component {
    constructor(props){
        super(props);
        this.state = {
            name:'Tahid',
            roll: this.props.roll
        };
    }

handleButton =  i =>  {
        this.setState({name:'shahid',roll:22});
    };
handleButtonArg = ()=>{
    this.handleButton();
}

    render() { 
        return (
            <>
            <h1>Name : {this.state.name}</h1>
            <h1>Roll : {this.state.roll}</h1>
            <button onClick={this.handleButtonArg}>Click Me</button>
            </>
          );
    }
}
 
export default App;
