import React, { Component } from 'react'
import './App.css'

export default class App extends Component {
    constructor(props) {
        super(props)
        let firstName = this.props.name.split(' ')[0].sp;
    
        this.state = {
             name : firstName
        }
        console.log(this.firstName)
    }
    
    render() {
        return (
            <div>
        <h1>{this.props.name}</h1>
        <h1>{this.state.name}</h1>
            </div>
        )
    }
}
