import React, {useState} from "react";
import "./Navbar.css";

function Navbar() {
  
  const [state, setstate] = useState(window.innerWidth);
  console.log(window.innerWidth)
  const getInnerWidth = () => {
    setstate(window.innerWidth);
    console.log(state)
  }
  return (
    <div className="navbar" onResize={getInnerWidth}>
      <img
        src="https://5studios.net/themes/laapp/img/logo-light.png"
        alt=""
        className="navbar__logo"
      />

      <i className={`fas fa-bars`}></i>

      <ul className="navbar__items">
        <NavItem item="Home" />
        <NavItem item="Features" />
        <NavItem item="Product" />
        <NavItem item="Pricing" />
        <NavItem item="Trial" />
      </ul>
    </div>
  );
}

const NavItem = ({ item }) => {
  return (
    <li>
      <a href={`#${item}`} className="navbar__item">
        {item}
      </a>
    </li>
  );
};

export default Navbar;
