import React, { useState } from "react";
import "./components.css";

function Home() {
  return (
    <div className="home">
      {/* Accordion */}
    <Accordion text='Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus dicta quibusdam excepturi rem sequi quis saepe sint fuga. Quo quis molestiae aliquid iste'
    heading="Section 1"/>
      {/* End */}
    </div>
  );
}

const Accordion = ({heading,text}) => {
  return (
    <>
      <button className={`accordion `}>
        {heading}
      </button>
      <div className="panel">
        <p>
          {text}
        </p>
      </div>
    </>
  );
};

export default Home;
