import React, { Component } from 'react'

export default class App extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             name: 'Tahid'
        }
    }
    stateChange = (prevState,props)

    render() {
        return (
            <div>
              <h1 onClick={this.stateChange}>  {this.state.name} </h1>
            </div>
        )
    }
}
