import React, { useState } from "react";
import "./components.css";

function Home() {
    const [active, setactive] = useState(false)
  return (
    <div className="home">
      {/* Accordion */}
      <Accordion
        text="Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus dicta quibusdam excepturi rem sequi quis saepe sint fuga. Quo quis molestiae aliquid iste"
        heading="Section 1"
        active={active}
        setactive={setactive()}
      />
      <Accordion
        text="Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus dicta quibusdam excepturi rem sequi quis saepe sint fuga. Quo quis molestiae aliquid iste"
        heading="Section 2"
        active={active}
      />
      <Accordion
        text="Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus dicta quibusdam excepturi rem sequi quis saepe sint fuga. Quo quis molestiae aliquid iste"
        heading="Section 3"
        active={active}
      />
      
      {/* End */}
    </div>
  );
}

const Accordion = ({ heading, text,active,setactive }) => {
  return (<>
      <button className={`accordion ${active?'active':''}`} onClick={()=>setactive(!active)}>{heading}</button>
      <div className="panel">
        <p>{text}</p>
      </div>
    </>
  )
};

export default Home;
