import React,{useState} from "react";
import "./Navbar.css";

function Navbar() {
  const [windowWidth, setwindowWidth] = useState(window.innerWidth);
  
  return (
   <>
   {window.innerWidth > 760 ? <DesktopNavbar />
                            : <h2> MObile view </h2>}
   </>
  );
}


const DesktopNavbar = () => {
  return <div className="navbar">
  <img
    src="https://5studios.net/themes/laapp/img/logo-light.png"
    alt=""
    className="navbar__logo"
  />
  <ul className="navbar__items">
    <NavItem item="Home"/>
    <NavItem item="Features"/>
    <NavItem item="Product"/>
    <NavItem item="Pricing"/>
    <NavItem item="Trial"/>
  </ul>
</div>
}


// const NavItem = ({ item }) => {
//   return (
//     <li>
//       <a href={`#${item}`} className="navbar__item">
//         {item}
//       </a>
//     </li>
//   );
// };
const NavItem = ({ item }) => {
  return (
    <li>
      <a href={`#${item}`} className="navbar__item">
        {item}
      </a>
    </li>
  );
};

export default Navbar;
