import React, { useState } from "react";
import "./components.css";

const projects = [
  {
    heading: "Facebook",
    info:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea   temporibus magnam quidem iure quasi?",
  },
  {
    heading: "Messenger",
    info:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea      temporibus magnam quidem iure quasi?",
  },
  {
    heading: "Todo-App",
    info:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea        temporibus magnam quidem iure quasi?",
  },
  {
    heading: "Twitter",
    info:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea temporibus magnam quidem iure quasi?",
  },
];

function Tabs() {
  const [click, setclick] = useState(false);
  const setTab = () => {
    setclick(!click);
  };
  return (
    <div className="tabs">
      <div className="tab">
        {projects.map((project) => {
          return <button onClick={setTab}>{project.heading}</button>;
        })}
      </div>

      {projects.map((project) => {
        return (
          <div className="tab__content" style={{ display: "block" }}>
            <h1>{project.heading}</h1>
            <p>{project.info}</p>
          </div>
        );
      })}
    </div>
  );
}

export default Tabs;
