import React, {useState,useEffect} from "react";
import "./Navbar.css";

function Navbar() {
  
  const [state, setstate] = useState(window.innerWidth);

  
    const getSize  = () => {
      
  window.onresize(setstate(window.innerWidth))
  console.log(state)
    }
    getSize()
  
  return (
    <div className="navbar" >
      <img
        src="https://5studios.net/themes/laapp/img/logo-light.png"
        alt=""
        className="navbar__logo"
      />

      <i className={`fas fa-bars`}></i>

      <ul className="navbar__items">
        <NavItem item="Home" />
        <NavItem item="Features" />
        <NavItem item="Product" />
        <NavItem item="Pricing" />
        <NavItem item="Trial" />
      </ul>
    </div>
  );
}

const NavItem = ({ item }) => {
  return (
    <li>
      <a href={`#${item}`} className="navbar__item">
        {item}
      </a>
    </li>
  );
};

export default Navbar;
