    },
    {
        heading: 'Twitter',
        info: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea temporibus magnam quidem iure quasi?'
    },
]

function Tabs() {
  const [click, setclick] = useState(false);
  const setTab = ()=>{
    setclick(!click);
    
  }
  return (
    <div className="tabs">
      <div className="tab">
       {projects.map(project=>{
           <button className="tablinks" onClick={setTab}>
               {project.heading}
           </button>
       })}
      </div>

      <div className="tab__content" id="messenger" style={{display:'block'}}>
        <h1>Messenger Clone</h1>
        <p>
          
        </p>
      </div>
      <div className="tab__content" id="facebook" style={click?{display:'block'}:{}}>
        <h1>Facebook Clone</h1>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea
          temporibus magnam quidem iure quasi?
        </p>
      </div>
      <div className="tab__content" id="todo__app">
        <h1>Todo App</h1>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea
          temporibus magnam quidem iure quasi?
        </p>
      </div>
      <div className="tab__content" id="portfolio">
        <h1>Portfolio</h1>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Eligendi, ea
          temporibus magnam quidem iure quasi?
        </p>
      </div>
    </div>
  );
}



export default Tabs;
