import React from "react";
import "./Fullscreennav.css";

function FullScreenNav() {
    
  const openNav = () => {
    document.querySelector(".overlay").style.width = "100%";
  };
  const closeNav = () => {
    document.querySelector(".overlay").style.width = "0%";
  };

  return (
    <div className="fullNav">
      <div className="overlay" id="my__nav">
          <a href='javascript:void(0)' onClick={closeNav} className="close__btn">
          &times;
        </a>

        <div className="overlay__content">
          <a href="javascript:void(0)">Item 1</a>
          <a href="javascript:void(0)">Item 2</a>
          <a href="javascript:void(0)">Item 3</a>
          <a href="javascript:void(0)">Item 4</a>
        </div>
      </div>
      <span onClick={openNav}>Clic</span>
    </div>
  );
}

export default FullScreenNav;
