import React, { useState } from "react";
import "./components.css";

function Navbar() {
  const [click, setclick] = useState(false);
  const [active, setactive] = useState(false);

  let prevScroll = window.pageYOffset;
  console.log('prev',prevScroll)
  window.onscroll = ()=>{
    let currentScroll = window.pageYOffset;
    conso
    document.querySelector('.icon__nav').style.top = prevScroll>currentScroll ? 0 : '-80px';

  }
  
  const responsive = ()=>{
    setclick(!click);
    const nav = document.querySelector('.icon__nav');
    nav.classList.toggle('responsive');
  }
  
  return (
    <div className="icon__nav">
      {/* Tooggling custom menuIcon */}
      <a
        href="#f"
        // useState --
        className={`bar__container ${click && "change"}`}
        onClick={responsive}

      >
        <div className="bar bar1"></div>
        <div className="bar bar2"></div>
        <div className="bar bar3"></div>
      </a>
      <a className={active?'active':''} onClick={()=>setactive(true)} href="/">
        <i className="fa fa-home"></i>
      </a>
      <a href="/about">
        <i className="fas fa-address-card"></i>
      </a>
      <a href="/services">
        <i className="fab fa-servicestack"></i>
      </a>
      <a href="/contact">
        <i className="fas fa-phone-square"></i>
      </a>
      <a href="/">
        <i className="fa fa-search"></i>
      </a>

      {/* Toggling menuIcon */}
      <a className="toggleIcon" href="#f" onClick={responsive}>
        <i className={`fas ${click ? "fa-times" : "fa-bars"}`}></i>
      </a>
    </div>
  );
}

export default Navbar;
