import React, { useState } from "react";
import "./components.css";

function Navbar() {
  const [click, setclick] = useState(false);
  return (
    <div className="icon__nav">
        {/* Tooggling custom menuIcon */}
      <a
        href="#f"

        // useState --
        className={`bar__container ${click && 'change'}`}
        onClick={() => setclick(!click)} 

        // reg JS

        // className="bar__container"
        // onClick={() =>
        //   document.querySelector(".bar__container").classList.toggle("change")
        // }
      >
        <div className="bar bar1"></div>
        <div className="bar bar2"></div>
        <div className="bar bar3"></div>
      </a>
      <a className="active" href="/">
        <i className="fa fa-home"></i>
      </a>
      <a href="#f">
        <i className="fa fa-search"></i>
      </a>
      <a href="#f">
        <i className="fa fa-envelope"></i>
      </a>
      <a href="#f">
        <i className="fa fa-globe"></i>
      </a>
      <a href="#f">
        <i className="fa fa-trash"></i>
      </a>

{/* Toggling menuIcon */}
      <a href="#f" onClick={() => setclick(!click)}>
        <i className={`fas ${click ? "fa-times" : "fa-bars"}`}></i>
      </a>
    </div>
  );
}

export default Navbar;
